graphql.query:
fragment Product on Product {
  productId
  name
  sku
  price {
    regular {
      value
      text
      __typename
    }
    special {
      value
      text
      __typename
    }
    __typename
  }
  image {
    url
    alt
    __typename
  }
  url
  inventory {
    isInStock
    __typename
  }
}
query Query($filters: [FilterInput]) {
  products(filters: $filters) {
    items {
      ...Product
      __typename
    }
    __typename
  }
}

query Query {
  myCart {
    ...ShoppingCart
    addItemApi
    addPaymentMethodApi
    addShippingMethodApi
    addContactInfoApi
    addAddressApi
    addNoteApi
    checkoutApi
    applyCouponApi
    removeCouponApi
    availableShippingMethods {
      code
      name
      cost {
        value
        text
        __typename
      }
      __typename
    }
    availablePaymentMethods {
      code
      name
      __typename
    }
    items {
      ...ShoppingCartItem
      cartItemId
      removeApi
      updateQtyApi
      errors
      __typename
    }
    __typename
  }
  customer: currentCustomer {
    customerId
    uuid
    email
    fullName
    groupId
    createdAt {
      value
      text
      __typename
    }
    addAddressApi
    addresses {
      addressId
      uuid
      fullName
      telephone
      address1
      address2
      city
      province {
        code
        name
        __typename
      }
      country {
        code
        name
        __typename
      }
      postcode
      isDefault
      updateApi
      deleteApi
      __typename
    }
    orders {
      ...ShoppingCart
      orderId
      status {
        name
        code
        badge
        __typename
      }
      orderNumber
      shipmentStatus {
        name
        code
        badge
        __typename
      }
      paymentStatus {
        name
        code
        badge
        __typename
      }
      items {
        ...ShoppingCartItem
        orderItemId
        __typename
      }
      __typename
    }
    __typename
  }
  themeConfig {
    copyRight
    __typename
  }
  addMineCartItemApi: url(routeId: 
"addMineCartItem")
  loginApi: url(routeId: 
"customerLoginJson")
  registerApi: url(routeId: 
"createCustomer")
  logoutApi: url(routeId: 
"customerLogoutJson")
}
